sys.path.insert(0,AVX::HELPER)
sys.path.insert(0,AVX::DEPENDENCIES)


null = None

client_ssl = '<%select_client_ssl%>'
server_ssl = '<%select_server_ssl%>'

tabular_input = '<%pool_mem_tabular%>'
pool_mem_list = ''

pool_name_val = '<%pool_name%>' if '<%pool_choice%>' == 'Create New' else '<%select_pool%>'
profiles = []
persistences = []
snat_pools = []
snat_pool_choice = '<%snat_pool%>'

def get_space_separated_values(multiselect_value):
    if multiselect_value == None or multiselect_value == '':
        return None
    space_separated_value = ""
    list_of_values = multiselect_value.split(",")
    for each_val in list_of_values:
        space_separated_value += each_val + " "

    return space_separated_value.strip()

def input_generator(key,list_of_obj):
    input_string = ""
    if len(list_of_obj) > 0:
        for each_obj in list_of_obj:
            input_string += each_obj + " "

        input_string = key + " { " + input_string.strip() + " }"

    if bool(input_string):
        return input_string
    else:
        return None


if __name__ == '__main__':

    # Profiles
    if client_ssl != None:
        profiles.extend(client_ssl.split(","))

    if server_ssl != None:
        profiles.extend(server_ssl.split(","))

    if '<%client_ssl_name%>' != None:
        profiles.append('<%client_ssl_name%>')

    if '<%server_ssl_name%>' != None:
        profiles.append('<%server_ssl_name%>')

        # Persistence
    if '<%select_persistence%>' != None:
        persistences.extend('<%select_persistence%>'.split(","))
    elif '<%persistence_name%>' != None:
        persistences.append('<%persistence_name%>')
    else:
        pass

    if '<%snat_pool_members%>' != None:
        snat_pools.extend('<%snat_pool_members%>'.split(','))



    if tabular_input != None and len(tabular_input) > 0:
        for each_row in tabular_input:
            pool_mem_list += each_row["pool_mem_ip"] + ":" + each_row["pool_mem_port"] + " "
    elif '<%pool_mem_ip%>' != '' and '<%pool_mem_port%>' != '' and '<%pool_mem_ip%>' != None and '<%pool_mem_port%>' != None:
        pool_mem_list = '<%pool_mem_ip%>' + ':'  + '<%pool_mem_port%>' + ' '
    else:
        pool_mem_list = None

    if pool_mem_list != None:
        pool_mem = 'add { ' + pool_mem_list + ' }'
    else:
        pool_mem = None

    if '<%select_monitors%>' != None:
        monitor_val = get_space_separated_values('<%select_monitors%>')
        monitor_val = monitor_val.replace(" "," and ")
    elif '<%monitor_name%>' != None:
        monitor_val = '<%monitor_name%>'
    else:
        monitor_val = None

    if '<%select_irule%>' != None:
        rules_val = '<%select_irule%>'.split(",")
    else:
        rules_val = None

    persistence_input = input_generator("replace-all-with",persistences)
    profiles_input = input_generator("add",profiles)
    snat_pool_members = input_generator("replace-all-with",snat_pools)

    if snat_pool_choice == 'Auto-Map':
        source_address_translation = { 'type': 'automap' }
    elif snat_pool_choice == "Create New":
        source_address_translation = { 'type':'snat','pool':'<%snat_pool_name%>'}
    else:
        source_address_translation = None

    AVX::LOG(str({"pool_mem":pool_mem,"profiles_input":profiles_input,"persistence_input":persistence_input,"snat_pool_members":snat_pool_members,"monitor_val":monitor_val,"pool_name_val":pool_name_val,"rules_val":rules_val,"source_address_translation":source_address_translation}))
