import json
from collections import defaultdict
null=None
result = defaultdict(lambda: defaultdict(list))

AVX::LOG("<%pool_and_pool_member_grid%>")
gtm = <%pool_and_pool_member_grid%>
pool_mem_selection = "<%pool_member_selection%>"
pool_tabular = []
pool_mem_tabular = []
if gtm is not None and len(gtm)>0:
    if pool_mem_selection =="Use Existing Virtual Server":
         pool_tabular = [{"name" : "<%gtm_pool_type%>".lower() +" " +i["gtm_pool_name"],"device_name" : "<%device_name%>","monitors":"<%select_monitors%>","members": i["pool_members"] } for i in gtm]
    else:
        pool_mem_tabular = [{"name" : i["pool_member_name"],"device_name" : "<%device_name%>","product": "<%pool_member_type%>", "virtual-servers" : 'add { ' + i["virtual_server_name"] +' { destination '+i["address_list"] +' } }'.replace('"',''),"addresses":"add { " +i["address_list"].split(":")[0] +" }", "datacenter" : "<%datacenter%>"} for i in gtm]
        pool_tabular = [{"name" : "<%gtm_pool_type%>".lower() +" " +i["gtm_pool_name"],"device_name" : "<%device_name%>","monitor":"<%select_monitors%>".replace(','," and "),"members": i["pool_member_name"]+":"+i["virtual_server_name"] } for i in gtm]

    for items in pool_tabular:
         result[items['name']]['members'].append(items['members'])

    pool_tabular = [
        {'name': name,'device_name': '<%device_name%>',"load-balancing-mode":"<%pool_members_preferred_load_balancing_method%>","alternate-mode":"<%pool_members_alternate_load_balancing_method%>","fallback-mode":"<%pool_members_fallback_load_balancing_method%>","monitor":"<%select_monitors%>".replace(','," and "), **{inner_names: 'add { '+' '.join(inner_values)+' }' for inner_names, inner_values in values.items()}}
        for name, values in result.items()
    ]

if "<%pool_selection%>" == "Create New":
    pool_mem_names = " ".join([i["name"].split(" ")[1] for i in pool_tabular])
    if pool_mem_names != "":
        pools = 'add { ' + pool_mem_names + ' }'
    else:
        pools = 'add { ' + "<%gtm_pool_name%>" + ' }'
else:
    pools =  'add { ' + "<%pool_list%>".replace(','," ") + ' }'
AVX::LOG(str(pool_mem_tabular))
AVX::LOG(str(pool_tabular))
AVX::LOG(str(pools))
AVX::OUTPUT({"pool_mem_tabular":pool_mem_tabular,"pool_tabular":pool_tabular,"pools": pools})
