sys.path.insert(0,AVX::HELPER)
sys.path.insert(0,AVX::DEPENDENCIES)

null = None


def get_space_separated_values(multiselect_value):
    if multiselect_value == None or multiselect_value == '':
        return None
    space_separated_value = ""
    list_of_values = multiselect_value.split(",")
    for each_val in list_of_values:
        space_separated_value += each_val + " "

    return space_separated_value.strip()




if '<%persistence%>' != None:
    persist_replace_val = 'replace-all-with {' + '<%persistence%>' + '}'
else:
    persist_replace_val = None

profiles = ""

if get_space_separated_values('<%client_ssl%>') != None and get_space_separated_values('<%server_ssl%>') != None:
    profiles += get_space_separated_values('<%client_ssl%>') + ' ' + get_space_separated_values('<%server_ssl%>')
elif get_space_separated_values('<%client_ssl%>') != None:
    profiles += get_space_separated_values('<%client_ssl%>')
elif get_space_separated_values('<%server_ssl%>') != None:
    profiles += get_space_separated_values('<%server_ssl%>')
else:
    profiles = None

if profiles != None:
    profiles_replace_val = 'replace-all-with {' + profiles + '}'
else:
    profiles_replace_val = None

tabular_input = '<%pool_mem_tabular%>'
pool_mem_list = ''

if tabular_input != None and len(tabular_input) > 0:
    for each_row in tabular_input:
        pool_mem_list += each_row["pool_mem_ip"] + ":" + each_row["pool_mem_port"] + " "
elif '<%pool_mem_ip%>' != '' and '<%pool_mem_port%>' != '' and '<%pool_mem_ip%>' != None and '<%pool_mem_port%>' != None:
    pool_mem_list = '<%pool_mem_ip%>' + ':'  + '<%pool_mem_port%>' + ' '
else:
    pool_mem_list = None

if pool_mem_list != None:
    pool_mem = 'replace-all-with { ' + pool_mem_list + ' }'
else:
    pool_mem = None

if '<%rule%>' != None:
    rules_val = '<%rule%>'.split(",")
else:
    rules_val = None

if '<%monitor_name%>' != None:
    monitor_val = get_space_separated_values('<%monitor_name%>')
    monitor_val = monitor_val.replace(" "," and ")
else:
    monitor_val = None

dnsName = '<%virtual_server%>'.split('.')[0]

AVX::LOG(str('<%pool_name%>'))
AVX::LOG(str({"pool_mem":pool_mem,"profiles_replace_val":profiles_replace_val,"persist_replace_val":persist_replace_val,"rules_val":rules_val,"monitor_val":monitor_val,"dnsName":dnsName}))    
